// Code ‚Äî Accumulate & Continue  (Playbook page)

// 1) Current HTTP page (children of the playbook page)
const http = $item(0).$node["HTTP ‚Äî List Children (cursor)"].json;

// 2) Previous state (before the HTTP call):
//    - On looped iterations: from the IF node
//    - On the first iteration: from the Init Cursor node
let prev;
try {
  prev = $item(0).$node["IF ‚Äî More Pages?"].json;
} catch {
  prev = $item(0).$node["Code ‚Äî Init Cursor"].json;
}

// 3) Accumulate blocks and compute next cursor
const all  = (prev.results || []).concat(http.results || []);
const next = (http?.has_more && http?.next_cursor) ? http.next_cursor : null;

// 4) Loop safety
const page = (prev._page ?? 0) + 1;
if (page > 40) throw new Error("Pagination exceeded 40 pages ‚Äì stopping.");
if (prev.startCursor && prev.startCursor === next) {
  throw new Error("Cursor did not advance ‚Äì stopping to avoid loop.");
}

// 5) Return updated state
return [{
  json: {
    pageId: prev.pageId,
    playbookId: prev.playbookId,
    startCursor: next,       // used by the HTTP node on the next pass
    results: all,            // full list of blocks across all pages
    done: !http?.has_more || !next,
    _page: page,
    },
}];

// 1) Prefer the structured shape produced by the OpenAI Chat node
const node = $item(0).$node["OpenAI ‚Äî Topic Extractor"]?.json;
let topics = node?.message?.content?.topics;

// 2) Fallbacks: if your model returns a JSON string instead of an object
if (!topics) {
  // common places where a string may appear
  const raw =
    node?.choices?.[0]?.message?.content ??
    node?.message?.content?.[0]?.text ?? // some drivers wrap text in array
    "";

  const cleaned = String(raw)
    .replace(/```json\s*/gi, "")
    .replace(/```/g, "")
    .trim();

  if (!cleaned) throw new Error("Topic extractor returned empty content.");

  let parsed;
  try {
    parsed = JSON.parse(cleaned);
  } catch (e) {
    throw new Error(`Bad topics JSON: ${e.message} | sample="${cleaned.slice(0,120)}..."`);
  }

  // allow either { topics: [...] } or [...] directly
  topics = Array.isArray(parsed) ? parsed : parsed?.topics;
}

// 3) Validate + normalize
const playbookId =
  $json.playbookId ??
  $item(0).$node["Code ‚Äî Init Cursor"]?.json?.playbookId ??
  $item(0).$node["Code ‚Äî Get Page ID"]?.json?.playbookId ??
  null;

if (!Array.isArray(topics)) throw new Error("Topic extractor did not return an array.");
if (topics.length < 5) throw new Error(`Expected 5 topics, got ${topics.length}.`);

return topics.slice(0, 5).map((t, i) => ({
  json: {
    topicIndex: i + 1,
    topic: t.topic || t.title || `Topic ${i + 1}`,
    one_liner: t.one_liner || t.oneLiner || "",
    problem: t.problem || "",
    outcome: t.outcome || "",
    key_ideas: t.key_ideas || t.keyIdeas || [],
    example: t.example || "",
    playbookId
  }
}));

// ---- read OpenAI output robustly (object or string) ----
const node = $item(0).$node["OpenAI ‚Äî LI Post + Carousel"].json;
const content = node?.message?.content ?? node?.content ?? {};
const obj = typeof content === "string"
  ? (() => { try { return JSON.parse(content); } catch { return {}; } })()
  : content;

// helpers
const toStr = (v) => (v == null ? "" : String(v));
const mdSlides = (arr = []) =>
  arr.map((s, i) => {
    const n = s.slide ?? i + 1;
    const title = toStr(s.title || "");
    const line = toStr(s.line || "");
    return `### Slide ${n}: ${title}\n${line}`;
  }).join("\n\n");

const playbookId =
  $json.playbookId ??
  $item(0).$node["Code ‚Äî Parse Topics JSON"]?.json?.playbookId ??
  $item(0).$node["Code ‚Äî Get Page ID"]?.json?.playbookId ??
  null;

const topic = $json.topic || `Topic ${$json.topicIndex}`;
const topicNo = $json.topicIndex;
const cta =
  $item(0).$node["Code ‚Äî Get Page ID"].json.primaryCta ||
  "Download the cheatsheet";

// normalize fields from OpenAI
const postText = toStr(obj.post || "");
const carouselArr = Array.isArray(obj.carousel) ? obj.carousel : [];
const meme = toStr(obj.visual?.meme || obj.meme || "");
const visCarousel = toStr(obj.visual?.carousel || "");

// ---- build 3 rows as plain strings for Notion ----
const rows = [
  {
    title: `${$('Split ‚Äî Topics (5)').first().json.topic} ‚Äî LinkedIn Post`,
    _assetType: "LinkedIn Post",
    topicNo,
    draft: postText.trim(),
    visual: visCarousel,
    _length: "",
    cta,
    distribution: ["LinkedIn", "Typefully"],
  },
  {
    title: `${$('Split ‚Äî Topics (5)').first().json.topic} ‚Äî Carousel`,
    _assetType: "Carousel Idea",
    topicNo,
    draft: mdSlides(carouselArr),          // ‚Üê markdown slides
    visual: visCarousel,
    _length: "5‚Äì7 slides",
    cta,
    distribution: ["LinkedIn", "Website"],
  },
  {
    title: `${$('Split ‚Äî Topics (5)').first().json.topic} ‚Äî Meme/Infographic`,
    _assetType: "Meme Hook",
    topicNo,
    draft: meme,
    visual: meme,
    _length: "1 image",
    cta,
    distribution: ["LinkedIn", "Website"],
  },
];

return rows.map(r => ({ json: { ...r, playbookId } }));

