// Build one Notion-ready row from "OpenAI ‚Äî Explainer Script"

// ---------- 1) Read OpenAI output robustly ----------
const node = $item(0).$node["OpenAI ‚Äî Explainer Script"].json;
const content = node?.message?.content ?? node?.content ?? {};

const ensureObject = (value) => {
  if (typeof value === "string") {
    try {
      return JSON.parse(value || "{}") || {};
    } catch {
      return {};
    }
  }
  return value && typeof value === "object" ? value : {};
};

const data = ensureObject(content);

// ---------- 2) Helpers ----------
const toStr = v => (v == null ? "" : String(v).trim());

// Accept array | string | object -> string[]
const asList = (v) => {
  if (Array.isArray(v)) return v.flat().map(toStr).filter(Boolean);
  if (typeof v === "string") {
    const s = v.replace(/^```[\s\S]*?\n|\n```$/g, ""); // strip code fences
    return s.split(/\r?\n+/)
            .map(x => x.replace(/^[\-\*\d\.\)\s]+/, "")) // strip bullets/nums
            .map(toStr)
            .filter(Boolean);
  }
  if (v && typeof v === "object") return Object.values(v).map(toStr).filter(Boolean);
  return [];
};

// Render a section if it has any items
const section = (items, label) =>
  items.length ? `## ${label}\n` + items.map(x => `- ${x}`).join("\n") : "";

// ---------- 3) Extract/normalize fields ----------
const script      = toStr(data.script);
const onScreen    = asList(data.on_screen);
const broll       = asList(data.broll);
const lowerThirds = asList(data.lower_thirds);

// ---------- 4) Build Visual Spec as markdown ----------
const visualMd = [
  section(onScreen, "On-Screen Text"),
  section(broll, "B-Roll / Visuals"),
  section(lowerThirds, "Lower Thirds")
].filter(Boolean).join("\n\n");

// ---------- 5) Meta ----------
const playbookId =
  $json.playbookId ??
  $item(0).$node["Code ‚Äî Parse Topics JSON"]?.json?.playbookId ??
  $item(0).$node["Code ‚Äî Get Page ID"]?.json?.playbookId ??
  null;
const topic      = $json.topic || `Topic ${$json.topicIndex || ""}`.trim();
const topicNo    = $json.topicIndex || null;
const primaryCta = $item(0).$node["Code ‚Äî Get Page ID"]?.json?.primaryCta || "Download the cheatsheet";

// ---------- 6) Return ONE row for Notion ----------
return [
  {
    json: {
      title: `${$('Code ‚Äî Parse Topics JSON').first().json.topic} ‚Äî Explainer`,
      _assetType: "Explainer Script",   // Notion Select
      topicNo: $('Code ‚Äî Parse Topics JSON').first().json.topicIndex, // Notion Number
      draft: script,                   // Notion Rich Text (Draft)
      visual: visualMd,                // Notion Rich Text (Visual Spec)
      _length: "3‚Äì5 min",              // Notion Text (Length)
      cta: primaryCta,                 // Notion Text (CTA)
      distribution: ["Loom", "LinkedIn"], // Notion Multi-select
      playbookId,
    }
  }
];

