// Build one Notion-ready row from "OpenAI ‚Äî Cheatsheet"

// 1) Read OpenAI output (handles object or stringified JSON)
const node = $item(0).$node["OpenAI ‚Äî Cheatsheet"].json;
const rawContent = node?.message?.content ?? node?.content ?? {};

const ensureObj = (v) => {
  if (typeof v === "string") {
    try { return JSON.parse(v); } catch { return {}; }
  }
  return v || {};
};
const data = ensureObj(rawContent);

// 2) Pull title and the content object
const playbookTitle = $item(0).$node["Code ‚Äî Get Page ID"]?.json?.title || "";
const title = data.title || (playbookTitle ? `${playbookTitle} ‚Äî Cheatsheet` : "Cheatsheet");
const bodyObj = ensureObj(data.content);

// 3) Sort numbered keys (e.g., "1. ‚Ä¶", "2. ‚Ä¶") and build markdown
const entries = Object.entries(bodyObj);

// helper to extract leading number for stable ordering
const num = (k) => {
  const m = String(k).trim().match(/^(\d+)[.)]/);
  return m ? parseInt(m[1], 10) : Number.MAX_SAFE_INTEGER;
};

entries.sort((a, b) => num(a[0]) - num(b[0]) || a[0].localeCompare(b[0]));

// markdown composer: H1 title + numbered sections
let md = `# ${title}\n\n`;
for (const [k, v] of entries) {
  const heading = String(k).trim();
  const text = typeof v === "string" ? v : JSON.stringify(v, null, 2);
  md += `### ${heading}\n${text}\n\n`;
}

// 4) Meta
const playbookId =
  $json.playbookId ??
  $item(0).$node["Code ‚Äî Parse Topics JSON"]?.json?.playbookId ??
  $item(0).$node["Code ‚Äî Get Page ID"]?.json?.playbookId ??
  null;

const primaryCta = $item(0).$node["Code ‚Äî Get Page ID"]?.json?.primaryCta || "Download the cheatsheet";

// 5) Return ONE row for Notion
return [{
  json: {
    title,                     // Title (Notion title)
    _assetType: "Cheatsheet",  // Select
    draft: md,                 // Rich Text: paste as Text (no annotations needed)
    visual: "",                // Visual Spec empty for cheatsheet
    _length: "1 page",         // Text
    cta: primaryCta,           // Text
    distribution: ["Website", "LinkedIn", "Gumroad"], // Multi-select
    playbookId,
  }
}];

